unit.struct:
struct node {
	int a[26], sz, y;
	char isSorted;
	char c;
	node *l, *r;
	node(char _c) : c(_c), y(rand() * RAND_MAX + rand()), sz(1), l(0), r(0), isSorted(0) {
		memset(a, 0, sizeof(a));
		a[c - 'a'] = 1;
	}
};

function.block:
{
	if(t && t->isSorted) {
		if(t->l) t->l->isSorted = t->isSorted;
		if(t->r) t->r->isSorted = t->isSorted;
		int arr[26];
		for(int i = 0; i < 26; ++i) {
			arr[i] = t->a[i];
		}
		if(t->isSorted == 1) {
			if(t->l) {
				for(int i = 0; i < 26; ++i) {
					t->l->a[i] = 0;
				}
				int sl = sz(t->l);
				for(int i = 0; i < 26 && sl != 0; ++i) {
					int mi = min(arr[i], sl);
					sl -= mi;
					arr[i] -= mi;
					t->l->a[i] = mi;
				}
			}
			for(int i = 0; i < 26; ++i) {
				if(arr[i] != 0) {
					t->c = (char)(i + 'a');
					arr[i]--;
					break;
				}
			}
			if(t->r) {
				for(int i = 0; i < 26; ++i) {
					t->r->a[i] = arr[i];
				}
			}
		} else {
			if(t->l) {
				for(int i = 0; i < 26; ++i) {
					t->l->a[i] = 0;
				}
				int sl = sz(t->l);
				for(int i = 25; i >= 0 && sl != 0; --i) {
					int mi = min(arr[i], sl);
					sl -= mi;
					arr[i] -= mi;
					t->l->a[i] = mi;
				}
			}
			for(int i = 25; i >= 0; --i) {
				if(arr[i] != 0) {
					t->c = (char)(i + 'a');
					arr[i]--;
					break;
				}
			}
			if(t->r) {
				for(int i = 25; i >= 0; --i) {
					t->r->a[i] = arr[i];
				}
			}
		}
		t->isSorted = false;
	}
}

{
	push(t);
	if(!t) {
		l = r = NULL;
		return;
	}
	if(sz(t->l) + 1 <= x) {
		split(t->r, t->r, r, x - sz(t->l) - 1);
		l = t;
		upd(l);
		return;
	}
	split(t->l, l, t->l, x);
	r = t;
	upd(r);
}

{
	push(l); push(r);
	if(!l || !r) {
		return l ? l : r;
	}
	if(l->y < r->y) {
		l->r = merge(l->r, r);
		upd(l);
		return l;
	}
	r->l = merge(l, r->l);
	upd(r);
	return r;
}

{
	if(!root) {
		return;
	}
	push(root);
	out(root->l);
	putchar(root->c);
	out(root->r);
}

{
//	freopen("input.txt", "r", stdin); freopen("output.txt", "w", stdout);
//	freopen("entropy.in", "r", stdin); freopen("entropy.out", "w", stdout);
	scanf("%d%d\n", &n, &q);
	gets(s + 1);
	node *root = NULL;
	for(int i = 1; i <= n; ++i) {
		root = merge(root, new node(s[i]));
	}
	while(q --> 0) {
		scanf("%d%d%d", &l, &r, &c);
		node *a, *b;
		split(root, a, root, l - 1);
		split(root, root, b, r - l + 1);
		if(c == 0) {
			root->isSorted = 2;
		} else {
			root->isSorted = 1;
		}
		root = merge(a, merge(root, b));
	}
	out(root);
	putchar('\n');
	getchar(); getchar();
	return 0;
}
