function.block:
{
   char c; int sgn;
   if(c = getchar(),c == EOF) return 0; //EOF
   while(c != '-' && (c < '0' || c > '9')) c = getchar();
   sgn = (c == '-') ? -1 : 1;
   ret = (c == '-') ? 0 : (c - '0');
   while(c = getchar(),c >= '0' && c <= '9') ret = ret * 10 + (c - '0');
   ret *= sgn;
   return 1;
}

{
    while(S(n)!=EOF)
    {
        S2(k,len);
        S(m);
        pii tp = mp(1,n);
        myset.insert(tp);
        int ans = -1;
        num = (tp.second - tp.first + 2) / (len + 1);
        bool flag = true;
        FI(m){
            scan_d(pri[i]);
            if(flag){
                it = myset.lower_bound(mp(pri[i],n + 1));
                it --;
                pii p = *it;
                {
                    myset.erase(it);
                    num -= (p.second - p.first + 2) / (len + 1);
                    if(p.first  <= pri[i] - 1){
                        pii tp = mp(p.first,pri[i] - 1);
                        num += (tp.second - tp.first + 2) / (len + 1);
                        myset.insert(tp);
                    }
                    if(pri[i] + 1 <= p.second){
                        pii tp = mp(pri[i] + 1,p.second);
                        num += (tp.second - tp.first + 2) / (len + 1);
                        myset.insert(tp);
                    }
                    if(num < k){
                        ans = i + 1;
                        flag = false;
                    }
                }
            }
        }
        printf("%d\n",ans);
    }
    return 0;
}
